import Login from "../components/authorization/Login"
import Axios from '../apis/Axios';
import jwt_decode from 'jwt-decode';
import { json, redirect } from 'react-router-dom';

const AuthenticationPage = () => {
    return <Login />

}
export default AuthenticationPage

export async function action({request}) {
    const searchParams = new URL(request.url).searchParams
    const mode = searchParams.get('mode') || 'login'

    if(mode !== 'login' && mode !== 'signup') {
        throw json({message: 'Unsupported mode.'}, {status: 422})
    }

    const data = await request.formData()
    
    const cred = {
        username: data.get('username'),
        password: data.get('password')
    }

        const response = await Axios.post('/korisnici/auth', cred)
        if(response.status === 422 || response.status === 401) {
            return response
        }
        if(response.status === 403) {
            throw json({message: 'Wrong username or password'})
        }
        if(response.status !== 200) {
            throw json({message: 'Could not authenticate user.'})
        }
        const jwt_decoded = jwt_decode(response.data)
        localStorage.setItem('jwt', response.data)
        localStorage.setItem('role', jwt_decoded.role.authority)
        const expiration = new Date()
        expiration.setHours(expiration.getHours()+1)
        localStorage.setItem('expiration', expiration.toISOString())
        return redirect('/')
    
}
