import { useLoaderData, json, defer, Await } from "react-router-dom";
import { Suspense } from "react";
import RestaurantItem from "../components/Restaurants/RestaurantItem";
import Card from "../components/UI/Card";

function RestaurantsPage() {
  const data = useLoaderData();
  const restaurants = data.restaurants

  return (
    <Card>
        <Suspense fallback={<p style={{textAlign: "center"}}>Loading...</p>}>
      <Await resolve={restaurants}>
    {(loadedRestaurants) => loadedRestaurants.map((restaurant) => (
        <RestaurantItem 
        id={restaurant.id}
        key={restaurant.id}
        name={restaurant.name}
        aboutUs={restaurant.aboutUs}
        location={restaurant.location}
        />
    ))}
  </Await>
    </Suspense>
    </Card>
  )

}

export default RestaurantsPage;

const loadRestaurants = async() => {
  const response = await fetch("http://localhost:8080/api/restaurants");

  if (!response.ok) {
    throw json(
      { message: "Could not fetch restaurants." },
      {
        status: 500,
      }
    );
  } else {
    const resData = await response.json()
    return resData
  }
}

export const loader = () => {
  return defer( {
    restaurants: loadRestaurants()
  })
};
