import { useState } from "react";
import { login } from "../../services/auth";
import { Form, Link, useNavigation, useSearchParams } from "react-router-dom";
import classes from './AuthForm.module.css';

const Login = () => {
    const navigation = useNavigation()

    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [searchParams] = useSearchParams()
    const isLogin = searchParams.get('mode') === 'login'
    const isSubmitting = navigation.state === 'submitting'

    const changeUsername = (event) => {
        setUsername(event.target.value)
    }

    const changePassword = event => {
        setPassword(event.target.value)
    }

    return(
        <>
        <Form method="post" className={classes.form}>
        <h1>{isLogin ? 'Log in' : 'Create a new user'}</h1>
        {/* {data && data.errors && <ul>
          {Object.values(data.errors).map(err => <li key={err}>{err}</li>)}
          </ul>}
        {data && data.message && <p>{data.message} </p>} */}
        <p>
          <label htmlFor="username">Username</label>
          <input id="username" type="text" name="username" required onChange={changeUsername}/>
        </p>
        <p>
          <label htmlFor="password">Password</label>
          <input id="password" type="password" name="password" required onChange={changePassword} />
        </p>
        <div className={classes.actions}>
          <Link to={`?mode=${isLogin ? 'signup' : 'login'}`}>
            {isLogin ? 'Create new user' : 'Login'}
          </Link>
          <button disabled={isSubmitting} onClick={() => login(username, password)}>{isSubmitting ? 'Submitting...' : 'Save'}</button>
        </div>
            
            </Form>        
        </>
    )

    // return(
    //     <Row className="justify-content-center">
    //         <Col md={6}>
    //         <Form>
    //             <Form.Group>
    //                 <Form.Label>Username</Form.Label>
    //                 <Form.Control type="text" name="username" onChange={(e)=>changeUsername(e.target.value)}></Form.Control>
    //             </Form.Group>
    //             <Form.Group>
    //                 <Form.Label>Password</Form.Label>
    //                 <Form.Control type="password" name="password" onChange={(e)=>changePassword(e.target.value)}></Form.Control>
    //             </Form.Group>
    //         </Form>
    //         <Button onClick={()=>login(username, password)}>Log in</Button>
    //         </Col>
    //     </Row>
    // )
}

export default Login;
