import jwt_decode from 'jwt-decode';
import { redirect } from 'react-router-dom';

export const login = async (username, password) => {
    const cred = {
        username: username,
        password: password
    }

    try {
        const response = await fetch('http://localhost:8080/api/korisnici/auth', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(cred)
        })
        const jwt_decoded = jwt_decode(response.data)
        window.localStorage.setItem('jwt', response.data)
        window.localStorage.setItem('role', jwt_decoded.role.authority)
        return redirect('/')
    } catch (error) {
        alert('Failed login')
        console.log(error);
    }

}

export const logout = () => {
    window.localStorage.removeItem('jwt')
    window.localStorage.removeItem('role')
    window.location.replace('/')
}
