import jwt_decode from 'jwt-decode';
import { redirect } from 'react-router-dom';

export const login = async (username, password) => {
    const cred = {
        username: username,
        password: password
    }

    try {
        const response = await Axios.post('korisnici/auth', cred)
        const jwt_decoded = jwt_decode(await response.json())
        localStorage.setItem('jwt', await response.json())
        localStorage.setItem('role', jwt_decoded.role.authority)
        return redirect('/')
    } catch (error) {
        alert('Failed login')
        console.log(error);
    }

}

export const logout = () => {
    localStorage.removeItem('jwt')
    localStorage.removeItem('role')
    return redirect('/')
}
